  avatar?: string;
  city: string;
  points: number;
  createdAt: Date;
}

export interface TurfOwner extends User {
  turfId: string;
  turfName: string;
}

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (email: string, password: string, role: UserRole) => Promise<boolean>;
  signup: (data: SignupData) => Promise<boolean>;
  logout: () => void;
  updateUser: (data: Partial<User>) => void;
  addPoints: (points: number) => void;
}

// Define types for stored user data to avoid "any"
interface SignupData {
  name: string;
  email: string;
  phone: string;
  password: string;
  city: string;
}

export interface StoredUser {
  id: string;
  name: string;
  email: string;
  phone: string;
  role: UserRole;
  city: string;
  points: number;
  createdAt: string; // Stored as string in local storage
  password?: string; // Present in registered users list
}

// Mock turf owner credentials
const TURF_OWNER_CREDENTIALS = [
  { id: "owner1", email: "elite@playpal.com", password: "elite123", turfId: "ahm-001", turfName: "Elite Cricket Arena" },
  { id: "owner2", email: "shuttle@playpal.com", password: "shuttle123", turfId: "ahm-002", turfName: "Shuttle Masters Badminton" },
  { id: "owner3", email: "goalzone@playpal.com", password: "goal123", turfId: "ahm-003", turfName: "Goal Zone Football Turf" },
  { id: "owner4", email: "diamond@playpal.com", password: "diamond123", turfId: "srt-001", turfName: "Diamond City Cricket Ground" },
  { id: "owner5", email: "baroda@playpal.com", password: "baroda123", turfId: "vad-001", turfName: "Baroda Cricket Academy" },
];

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider = ({ children }: { children: ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Load user from localStorage on mount
    const storedUser = localStorage.getItem("playpal_user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser({
          ...parsedUser,
          createdAt: new Date(parsedUser.createdAt)
        });
      } catch (e) {
        localStorage.removeItem("playpal_user");
      }
    }
    setIsLoading(false);
  }, []);

  const login = async (email: string, password: string, role: UserRole): Promise<boolean> => {
    setIsLoading(true);

    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 800));

    if (role === "turf_owner") {
      // Check turf owner credentials
      const owner = TURF_OWNER_CREDENTIALS.find(
        o => o.email === email && o.password === password
      );

      if (owner) {
        const turfOwner: TurfOwner = {
          id: owner.id,
          name: owner.turfName + " Admin",
          email: owner.email,
          phone: "+91 98765 00000",
          role: "turf_owner",
          city: "Ahmedabad",
          points: 0,
          createdAt: new Date(),
          turfId: owner.turfId,
          turfName: owner.turfName
        };
        setUser(turfOwner);
        localStorage.setItem("playpal_user", JSON.stringify(turfOwner));
        setIsLoading(false);
        return true;
      }
    } else {
      // User login - check localStorage for registered users
      const registeredUsers: StoredUser[] = JSON.parse(localStorage.getItem("playpal_registered_users") || "[]");
      const foundUser = registeredUsers.find(
        (u) => u.email === email && u.password === password
      );

      if (foundUser) {
        const loggedInUser: User = {
          id: foundUser.id,
          name: foundUser.name,
          email: foundUser.email,
          phone: foundUser.phone,
          role: "user",
          city: foundUser.city,
          points: foundUser.points || 100,
          createdAt: new Date(foundUser.createdAt)
        };
        setUser(loggedInUser);
        localStorage.setItem("playpal_user", JSON.stringify(loggedInUser));
        setIsLoading(false);
        return true;
      }
    }

    setIsLoading(false);
    return false;
  };

  const signup = async (data: SignupData): Promise<boolean> => {
    setIsLoading(true);

    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 800));

    const registeredUsers = JSON.parse(localStorage.getItem("playpal_registered_users") || "[]");

    // Check if email already exists
    if (registeredUsers.some((u: StoredUser) => u.email === data.email)) {
      setIsLoading(false);
      return false;
    }

    const newUser = {
      id: `user_${Date.now()}`,
      name: data.name,
      email: data.email,
      phone: data.phone,
      password: data.password,
      city: data.city,
      points: 100, // Welcome bonus
      createdAt: new Date().toISOString()
    };

    registeredUsers.push(newUser);
    localStorage.setItem("playpal_registered_users", JSON.stringify(registeredUsers));

    const loggedInUser: User = {
      id: newUser.id,
      name: newUser.name,
      email: newUser.email,
      phone: newUser.phone,
      role: "user",
      city: newUser.city,
      points: newUser.points,
      createdAt: new Date()
    };

    setUser(loggedInUser);
    localStorage.setItem("playpal_user", JSON.stringify(loggedInUser));
    setIsLoading(false);
    return true;
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("playpal_user");
  };

  const updateUser = (data: Partial<User>) => {
    if (user) {
      const updatedUser = { ...user, ...data };
      setUser(updatedUser);
      localStorage.setItem("playpal_user", JSON.stringify(updatedUser));
    }
  };

  const addPoints = (points: number) => {
    if (user) {
      const updatedUser = { ...user, points: user.points + points };
      setUser(updatedUser);
      localStorage.setItem("playpal_user", JSON.stringify(updatedUser));
    }
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isAuthenticated: !!user,
        isLoading,
        login,
        signup,
        logout,
        updateUser,
        addPoints
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

import React, { createContext, useContext, useState, useEffect, ReactNode } from "react";

export interface Booking {
  id: string;
  venueId: string;
  venueName: string;
  sport: string;
  date: string;
  timeSlot: string;
  duration: number; // in hours
  totalAmount: number;
  paidAmount: number;
  remainingAmount: number;
  status: "Confirmed" | "Completed" | "Cancelled" | "Pending";
  userId: string;
  userName: string;
  userPhone: string;
  createdAt: string;
  isManual?: boolean; // For turf owner manual bookings
}

export interface TimeSlot {
  time: string;
  isAvailable: boolean;
  price: number;
}

interface BookingContextType {
  bookings: Booking[];
  addBooking: (booking: Omit<Booking, "id" | "createdAt">) => Booking;
  cancelBooking: (bookingId: string) => void;
  completeBooking: (bookingId: string) => void;
  getBookingsByUser: (userId: string) => Booking[];
  getBookingsByVenue: (venueId: string) => Booking[];
  getBookingsByDate: (venueId: string, date: string) => Booking[];
  getAvailableSlots: (venueId: string, date: string) => TimeSlot[];
  isSlotAvailable: (venueId: string, date: string, time: string) => boolean;
}

const BookingContext = createContext<BookingContextType | undefined>(undefined);

// Generate time slots from 6 AM to 11 PM
const generateTimeSlots = (): string[] => {
  const slots: string[] = [];
  for (let hour = 6; hour <= 23; hour++) {
    const time = `${hour.toString().padStart(2, '0')}:00`;
    slots.push(time);
  }
  return slots;
};

const ALL_TIME_SLOTS = generateTimeSlots();

export const BookingProvider = ({ children }: { children: ReactNode }) => {
  const [bookings, setBookings] = useState<Booking[]>([]);

  useEffect(() => {
    // Load bookings from localStorage
    const storedBookings = localStorage.getItem("playpal_bookings");
    if (storedBookings) {
      try {
        setBookings(JSON.parse(storedBookings));
      } catch (e) {
        localStorage.removeItem("playpal_bookings");
      }
    }
  }, []);

  const saveBookings = (newBookings: Booking[]) => {
    setBookings(newBookings);
    localStorage.setItem("playpal_bookings", JSON.stringify(newBookings));
  };

  const addBooking = (bookingData: Omit<Booking, "id" | "createdAt">): Booking => {
    const newBooking: Booking = {
      ...bookingData,
      id: `booking_${Date.now()}`,
      createdAt: new Date().toISOString()
    };

    const updatedBookings = [...bookings, newBooking];
    saveBookings(updatedBookings);
    return newBooking;
  };

  const cancelBooking = (bookingId: string) => {
    const updatedBookings = bookings.map(b =>
      b.id === bookingId ? { ...b, status: "Cancelled" as const } : b
    );
    saveBookings(updatedBookings);
  };

  const completeBooking = (bookingId: string) => {
    const updatedBookings = bookings.map(b =>
      b.id === bookingId ? { ...b, status: "Completed" as const, paidAmount: b.totalAmount, remainingAmount: 0 } : b
    );
    saveBookings(updatedBookings);
  };

  const getBookingsByUser = (userId: string): Booking[] => {
    return bookings.filter(b => b.userId === userId);
  };

  const getBookingsByVenue = (venueId: string): Booking[] => {
    return bookings.filter(b => b.venueId === venueId);
  };

  const getBookingsByDate = (venueId: string, date: string): Booking[] => {
    return bookings.filter(b =>
      b.venueId === venueId &&
      b.date === date &&
      b.status !== "Cancelled"
    );
  };

  const getAvailableSlots = (venueId: string, date: string): TimeSlot[] => {
    const dateBookings = getBookingsByDate(venueId, date);
    const bookedSlots = new Set(dateBookings.map(b => b.timeSlot));

    return ALL_TIME_SLOTS.map(time => ({
      time,
      isAvailable: !bookedSlots.has(time),
      price: getSlotPrice(time)
    }));
  };

  const getSlotPrice = (time: string): number => {
    const hour = parseInt(time.split(':')[0]);
    // Peak hours (6-10 AM, 5-10 PM) cost more
    if ((hour >= 6 && hour <= 10) || (hour >= 17 && hour <= 22)) {
      return 1500; // Peak price
    }
    return 1000; // Off-peak price
  };

  const isSlotAvailable = (venueId: string, date: string, time: string): boolean => {
    const slots = getAvailableSlots(venueId, date);
    const slot = slots.find(s => s.time === time);
    return slot?.isAvailable ?? false;
  };

  return (
    <BookingContext.Provider
      value={{
        bookings,
        addBooking,
        cancelBooking,
        completeBooking,
        getBookingsByUser,
        getBookingsByVenue,
        getBookingsByDate,
        getAvailableSlots,
        isSlotAvailable
      }}
    >
      {children}
    </BookingContext.Provider>
  );
};

export const useBooking = () => {
  const context = useContext(BookingContext);
  if (context === undefined) {
    throw new Error("useBooking must be used within a BookingProvider");
  }
  return context;
};

import React, { createContext, useContext, useState, useEffect, ReactNode } from "react";
import { gujaratCities, City } from "@/data/venues";

interface LocationContextType {
  selectedCity: City;
  setSelectedCity: (city: City) => void;
  cities: City[];
  detectLocation: () => void;
}

const LocationContext = createContext<LocationContextType | undefined>(undefined);

export const LocationProvider = ({ children }: { children: ReactNode }) => {
  const [selectedCity, setSelectedCity] = useState<City>(gujaratCities[0]); // Default to Ahmedabad

  useEffect(() => {
    // Load saved city from localStorage
    const savedCity = localStorage.getItem("playpal_city");
    if (savedCity) {
      try {
        const city = JSON.parse(savedCity);
        const found = gujaratCities.find(c => c.name === city.name);
        if (found) {
          setSelectedCity(found);
        }
      } catch (e) {
        // Ignore parse errors
      }
    }
  }, []);

  const handleSetCity = (city: City) => {
    setSelectedCity(city);
    localStorage.setItem("playpal_city", JSON.stringify(city));
  };

  const detectLocation = () => {
    // In a real app, we would use the Geolocation API
    // For now, we'll just default to Ahmedabad
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          // Mock: Just set to Ahmedabad for demo
          // In production, you'd use reverse geocoding
          handleSetCity(gujaratCities[0]);
        },
        (error) => {
          console.log("Location detection failed, using default");
          handleSetCity(gujaratCities[0]);
        }
      );
    }
  };

  return (
    <LocationContext.Provider
      value={{
        selectedCity,
        setSelectedCity: handleSetCity,
        cities: gujaratCities,
        detectLocation
      }}
    >
      {children}
    </LocationContext.Provider>
  );
};

export const useLocation = () => {
  const context = useContext(LocationContext);
  if (context === undefined) {
    throw new Error("useLocation must be used within a LocationProvider");
  }
  return context;
};

import { useRef, useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useBooking } from "@/contexts/BookingContext";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { CheckCircle2, Download, Home, Printer, Share2 } from "lucide-react";
import { format } from "date-fns";
import { motion } from "framer-motion";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";
import { toast } from "sonner";

const Invoice = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { bookings } = useBooking();
    const [booking, setBooking] = useState<any>(null);
    const invoiceRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        if (id) {
            const foundBooking = bookings.find((b) => b.id === id);
            if (foundBooking) {
                setBooking(foundBooking);
            } else {
                toast.error("Booking not found");
                navigate("/dashboard");
            }
        }
    }, [id, bookings, navigate]);

    const handleDownloadOriginal = () => {
        window.print();
    };

    const handleDownloadPDF = async () => {
        if (!invoiceRef.current) return;

        try {
            const canvas = await html2canvas(invoiceRef.current, {
                scale: 2,
                logging: false,
                useCORS: true,
                backgroundColor: "#ffffff"
            });

            const imgData = canvas.toDataURL("image/png");
            const pdf = new jsPDF({
                orientation: "portrait",
                unit: "mm",
                format: "a4"
            });

            const imgWidth = 210;
            const imgHeight = (canvas.height * imgWidth) / canvas.width;

            pdf.addImage(imgData, "PNG", 0, 0, imgWidth, imgHeight);
            pdf.save(`PlayPal-Invoice-${booking?.id || "download"}.pdf`);
            toast.success("Invoice downloaded successfully");
        } catch (error) {
            console.error("Error generating PDF:", error);
            toast.error("Failed to generate PDF");
        }
    };

    if (!booking) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="animate-spin h-8 w-8 border-4 border-primary border-t-transparent rounded-full"></div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-secondary/30 py-12 px-4 sm:px-6 lg:px-8 flex flex-col items-center justify-center">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                className="w-full max-w-2xl"
            >
                <div className="text-center mb-8">
                    <motion.div
                        initial={{ scale: 0 }}
                        animate={{ scale: 1 }}
                        transition={{ type: "spring", stiffness: 200, damping: 20, delay: 0.2 }}
                        className="w-16 h-16 bg-primary rounded-full flex items-center justify-center mx-auto mb-4"
                    >
                        <CheckCircle2 className="w-8 h-8 text-white" />
                    </motion.div>
                    <h1 className="text-3xl font-bold text-foreground">Booking Confirmed!</h1>
                    <p className="text-muted-foreground mt-2">
                        Your booking at {booking.venueName} has been successfully confirmed.
                    </p>
                </div>

                <Card className="shadow-lg border-border/50 overflow-hidden" ref={invoiceRef}>
                    <div className="bg-primary/5 p-6 border-b border-border/50">
                        <div className="flex justify-between items-start">
                            <div>
                                <h2 className="text-2xl font-bold text-primary">PlayPal</h2>
                                <p className="text-sm text-muted-foreground mt-1">Sports Booking Platform</p>
                            </div>
                            <div className="text-right">
                                <h3 className="text-sm font-medium text-muted-foreground">INVOICE</h3>
                                <p className="text-lg font-bold font-mono">#{booking.id.slice(-6).toUpperCase()}</p>
                                <p className="text-xs text-muted-foreground mt-1">
                                    {format(new Date(booking.createdAt), "PPP")}
                                </p>
                            </div>
                        </div>
                    </div>

                    <CardContent className="p-6 md:p-8 space-y-8">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                            <div>
                                <h4 className="text-sm font-semibold text-muted-foreground uppercase tracking-wider mb-3">
                                    Customer Details
                                </h4>
                                <div className="space-y-1">
                                    <p className="font-medium text-lg">{booking.userName}</p>
                                    <p className="text-muted-foreground text-sm">{booking.userPhone}</p>
                                    <div className="mt-2 text-xs px-2 py-1 bg-primary/10 text-primary rounded inline-block">
                                        Verified Customer
