                                        {booking.status}
                                    </div>
                                </div>
                            </div>
                        </div>

                        <Separator />

                        <div>
                            <h4 className="text-sm font-semibold text-muted-foreground uppercase tracking-wider mb-4">
                                Payment Summary
                            </h4>
                            <div className="bg-secondary/20 rounded-lg p-4 space-y-3">
